mapper.select:
SELECT count(m_id)
	FROM members
	WHERE m_id = #{id}

SELECT count(m_email)
	FROM members
	WHERE m_email = #{email}

SELECT COUNT(email)
	FROM emailauthcode
	WHERE authCode = #{authCode} 
		AND email=#{email}

mapper.update:
UPDATE emailauthcode
	SET authCode = #{authCode}
	WHERE email = #{email}

mapper.select:
SELECT count(email)
	FROM emailauthcode
	WHERE email = #{email}
